mapper.update:
update dish
name= #{name}, category_id=#{categoryId}, price=#{price}, image=#{image}, description=#{description}, update_time=#{updateTime}, update_user=#{updateUser}, status=#{status}
where id = #{id}

mapper.select:
select dish.*,c.name as categoryName from dish
inner join `takeaway-test`.category c on dish.category_id = c.id


        and dish.name like concat('%',#{name},'%')


        and c.category_id = #{categoryId}


        and dish.status = #{status}


order by dish.create_time desc
